  //     favorite: true
  //   },

  //   {
  //     text: "Выучить JS",
  //     favorite: false
  //   }
  // ];

  const [todos, setTodos] = useState([
    {
      text: "Купить бананы",
      favorite: false
    },

    {
      text: "Купить дом",
      favorite: true
    },

    {
      text: "Выучить JS",
      favorite: false
    }
  ])

  const deleteTodo = (indexOfItem) => {
    const filtered = todos.filter((todo, index) => {
      if (index === indexOfItem) {
        return false;
      }
      return true;
    })
    setTodos(filtered);
  };

  const makeFavorite = (i) => {
    const newTodos = todos.map((item, index) => {
      if (i === index) {
        return {
          ...item,
          favorite: !item.favorite
        }
      }
      return item;
    })
    setTodos(newTodos)
  }

  const newTodos = todos.map((todo ,index) => {

    return(
      <div className={`todo ${todo.favorite ? 'selected': ''}`}>
      <div className="favorite">
        <button onClick={() => makeFavorite(index)}>★</button>
      </div>
      <div className="todo-text">
        {todo.text}
      </div>
      <div className="actions">
        <button onClick={() => deleteTodo(index)}>✕</button>
      </div>
      </div>
    )
  })

  return (
    <div className="app">
      <div className="header">Список дел</div>
      <div className="form">
        <input type='text'
        value={text}
