import React from "react";
import './styles.css';

function App() {
  const todos = [
    {
      text: "Купить бананы",
      favorite: false
    },

    {
      text: "Купить дом",
      favorite: true
    },

    {
      text: "Выучить JS",
      favorite: false
    }
  ];

  const deleteTodo = () => {
    alert();
  }
  const newTodos = todos.map((todo) => {
  const todoClass = todo.favorite ? 'todo selected' : 'todo';

    return(
      <div className={todoClass}>
      <div className="favorite">
        <span>★</span>
      </div>
      <div className="todo-text">
        {todo.text}
      </div>
      <div className="actions">
        <button onClick={deleteTodo}>✕</button>
      </div>
      </div>
    )
  })

  return (
    <div className="app">
      <div className="header">Список дел</div>
      <div className="form">
        <input type='text' />
          <button>
            Добавить
          </button>
      </div>
      <div className="todos">
        {newTodos}
      </div>
    </div>
  );
}

export default App;
