  //   }
  // ];

  const [todos, setTodos] = useState([
    {
      text: "Купить бананы",
      favorite: false
    },

    {
      text: "Купить дом",
      favorite: true
    },

    {
      text: "Выучить JS",
      favorite: false
    }
  ])

  const [text, setText] = useState('');

  const deleteTodo = (indexOfItem) => {
    const filtered = todos.filter((todo, index) => {
      if (index === indexOfItem) {
        return false;
      }
      return true;
    })
    setTodos(filtered);
  };

  const makeFavorite = (i) => {
    const newTodos = todos.map((item, index) => {
      if (i === index) {
        return {
          ...item,
          favorite: !item.favorite
        }
      }
      return item;
    })
    setTodos(newTodos)
  }


  const addTodo = () => {
    setTodos([...todos, {
      text: text,
      favorite: false
    }])
    setText('')
  }

  return (
    <div className="app">
      <Header />
      <Form text={text} setTex={setText} addTodo={addTodo} />
      <div className="todos">
        {todos.map((todo ,index) => {

return(
  <div className={`todo ${todo.favorite ? 'selected': ''}`}>
  <div className="favorite">
    <button onClick={() => makeFavorite(index)}>★</button>
  </div>
  <div className="todo-text">
    {todo.text}
  </div>
  <div className="actions">
    <button onClick={() => deleteTodo(index)}>✕</button>
  </div>
  </div>
)
})
}
      </div>
    </div>
  );
}

export default App;
