import React from "react";
import './styles.css';

function App() {
  const todos = [
    {
      text: "Купить бананы",
      favorite: false
    },

    {
      text: "Купить дом",
      favorite: false
    },

    {
      text: "Выучить JS",
      favorite: false
    }
  ];

  const newTodos = todos.map((todo) => {
    return(
      <div className="todo">
      <div className="favorite">
        <span>★</span>
      </div>
      <div className="todo-text">
        Купить бананы
      </div>
      <div className="actions">
        <span>✕</span>
      </div>
      </div>
    )
  })

  return (
    <div className="app">
      <div className="header">Список дел</div>
      <div className="form">
        <input type='text' />
          <button>
            Добавить
          </button>
      </div>
      <div className="todos">

      </div>
    </div>
  );
}

export default App;
