import React, { useState } from "react";
import Form from "./Form";
import Header from "./Header";
import './styles.css';
import Todos from "./Todos";

function App() {
  // const todos = [
  //   {
  //     text: "Купить бананы",
  //     favorite: false
  //   },

  //   {
  //     text: "Купить дом",
  //     favorite: true
  //   },

  //   {
  //     text: "Выучить JS",
  //     favorite: false
  //   }
  // ];

  const [todos, setTodos] = useState([
    {
      text: "Купить бананы",
      favorite: false
    },

    {
      text: "Купить дом",
      favorite: true
    },

    {
      text: "Выучить JS",
      favorite: false
    }
  ])

  const [text, setText] = useState('');

  const deleteTodo = (indexOfItem) => {
    const filtered = todos.filter((todo, index) => {
      if (index === indexOfItem) {
        return false;
      }
      return true;
    })
    setTodos(filtered);
  };

  const makeFavorite = (i) => {
    const newTodos = todos.map((item, index) => {
      if (i === index) {
        return {
          ...item,
          favorite: !item.favorite
        }
      }
      return item;
    })
    setTodos(newTodos)
  }


  const addTodo = () => {
    for (let i = 0; i < todos.length; i++) {
    if (text && text !== todos ) {
    setTodos([...todos, {
      text: text,
      favorite: false
    }])
    setText('')
  }
  }}

  return (
    <div className="app">
      <Header />
      <Form text={text} setText={setText} addTodo={addTodo} />
      <Todos todos={todos} makeFavorite={makeFavorite} deleteTodo={deleteTodo} />
    </div>
  );
}

export default App;
